    private void btn_selanjutnyaMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_btn_selanjutnyaMouseClicked
        //insert into akun
         if(!txt_email.getText().contains("@")){
            lbl_emailconfirm.setText("Masukkan email dengan benar");
        }else{
            lbl_emailconfirm.setText("");
       try{
           String sql="INSERT INTO akun (`nama_toko`, `nama_pemilik`, `alamat`, `username`, `email`, `password`) VALUES ('"
                    +txt_nama_toko.getText()+"','"
                    +txt_nama_pemilik.getText()+"','"
                    +txt_alamat.getText()+"','"
                    +txt_username.getText()+"','"
                    +txt_email.getText()+"','"
                    +txt_password.getText()+"')";
            java.sql.Connection conn= (Connection) koneksi.configDB();
            java.sql.PreparedStatement pst=conn.prepareStatement(sql);
            pst.execute();
            JOptionPane.showMessageDialog(null, "Berhasil membuat akun.\nSilahkan login kembali");
            this.setVisible(false);
            new Login().setVisible(true);
        }catch(Exception e) {
            JOptionPane.showMessageDialog(this, e.getMessage());
        }
                }
    }//GEN-LAST:event_btn_selanjutnyaMouseClicked

