    private void txt_emailKeyReleased(java.awt.event.KeyEvent evt) {//GEN-FIRST:event_txt_emailKeyReleased
        // TODO add your handling code here:
        if(!txt_email.getText().contains("@")){
            btn_selanjutnya.setIcon(new javax.swing.ImageIcon(getClass().getResource("/img/selanjutnya off.png")));
            btn_selanjutnya.setCursor(Cursor.getPredefinedCursor(Cursor.DEFAULT_CURSOR));
            lbl_emailconfirm.setText("Masukkan email dengan benar");
        }else{
            btn_selanjutnya.setIcon(new javax.swing.ImageIcon(getClass().getResource("/img/btn_selanjutnya.png")));
            btn_selanjutnya.setCursor(Cursor.getPredefinedCursor(Cursor.HAND_CURSOR));
            lbl_emailconfirm.setText("");
        }
    }//GEN-LAST:event_txt_emailKeyReleased

